function [Ksr,Ksc,Ksv,kc,Qsr,Qsc,Qsv,qc,Fsr,Fsv,fc] = assmbl_ful(iele,nnpe,np,se,qe,fe,Ksr,Ksc,Ksv,kc,Qsr,Qsc,Qsv,qc,Fsr,Fsv,fc)% assembles elemental force and stiffness matrices into global counterpartsnnpe3=3*nnpe;nnpe2=2*nnpe;for  i=1:1:nnpe	    i4=4*np(iele,i);    i3=i4-1;    i2=i3-1;    i1=i2-1;          Fsr(fc)=i1;     Fsv(fc)=fe(i);     fc=fc+1;    Fsr(fc)=i2;     Fsv(fc)=fe(i+nnpe);       fc=fc+1;            Fsr(fc)=i3;     Fsv(fc)=fe(i+nnpe2);     fc=fc+1;    Fsr(fc)=i4;     Fsv(fc)=fe(i+nnpe3);     fc=fc+1;	      for j=1:1:nnpe	           j4=4*np(iele,j);          j3=j4-1;        j2=j3-1;	          j1=j2-1;              Ksr(kc)=i1;         Ksc(kc)=j1;         Ksv(kc)=se(i,j);        kc=kc+1;        Ksr(kc)=i1;         Ksc(kc)=j2;         Ksv(kc)=se(i,j+nnpe);         kc=kc+1;        Ksr(kc)=i1;         Ksc(kc)=j3;         Ksv(kc)=se(i,j+nnpe2);         kc=kc+1;        Ksr(kc)=i1;        Ksc(kc)=j4;         Ksv(kc)=se(i,j+nnpe3);         kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j1;        Ksv(kc)=se(i+nnpe,j);          kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j2;         Ksv(kc)=se(i+nnpe,j+nnpe);         kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j3;         Ksv(kc)=se(i+nnpe,j+nnpe2);         kc=kc+1;        Ksr(kc)=i2;         Ksc(kc)=j4;         Ksv(kc)=se(i+nnpe,j+nnpe3);         kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j1;        Ksv(kc)=se(i+nnpe2,j);          kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j2;         Ksv(kc)=se(i+nnpe2,j+nnpe);         kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j3;         Ksv(kc)=se(i+nnpe2,j+nnpe2);         kc=kc+1;        Ksr(kc)=i3;         Ksc(kc)=j4;         Ksv(kc)=se(i+nnpe2,j+nnpe3);         kc=kc+1;                        Ksr(kc)=i4;         Ksc(kc)=j1;        Ksv(kc)=se(i+nnpe3,j);          kc=kc+1;        Ksr(kc)=i4;         Ksc(kc)=j2;         Ksv(kc)=se(i+nnpe3,j+nnpe);         kc=kc+1;        Ksr(kc)=i4;         Ksc(kc)=j3;         Ksv(kc)=se(i+nnpe3,j+nnpe2);         kc=kc+1;        Ksr(kc)=i4;         Ksc(kc)=j4;         Ksv(kc)=se(i+nnpe3,j+nnpe3);         kc=kc+1;                                                                Qsr(qc)=i1;         Qsc(qc)=j1;         Qsv(qc)=qe(i,j);        qc=qc+1;        Qsr(qc)=i1;         Qsc(qc)=j2;         Qsv(qc)=qe(i,j+nnpe);         qc=qc+1;        Qsr(qc)=i1;         Qsc(qc)=j3;         Qsv(qc)=qe(i,j+nnpe2);         qc=qc+1;        Qsr(qc)=i1;        Qsc(qc)=j4;         Qsv(qc)=qe(i,j+nnpe3);         qc=qc+1;        Qsr(qc)=i2;         Qsc(qc)=j1;        Qsv(qc)=qe(i+nnpe,j);          qc=qc+1;        Qsr(qc)=i2;         Qsc(qc)=j2;         Qsv(qc)=qe(i+nnpe,j+nnpe);         qc=qc+1;        Qsr(qc)=i2;         Qsc(qc)=j3;         Qsv(qc)=qe(i+nnpe,j+nnpe2);         qc=qc+1;        Qsr(qc)=i2;         Qsc(qc)=j4;         Qsv(qc)=qe(i+nnpe,j+nnpe3);         qc=qc+1;        Qsr(qc)=i3;         Qsc(qc)=j1;        Qsv(qc)=qe(i+nnpe2,j);          qc=qc+1;        Qsr(qc)=i3;         Qsc(qc)=j2;         Qsv(qc)=qe(i+nnpe2,j+nnpe);         qc=qc+1;        Qsr(qc)=i3;         Qsc(qc)=j3;         Qsv(qc)=qe(i+nnpe2,j+nnpe2);         qc=qc+1;        Qsr(qc)=i3;         Qsc(qc)=j4;         Qsv(qc)=qe(i+nnpe2,j+nnpe3);         qc=qc+1;                        Qsr(qc)=i4;         Qsc(qc)=j1;        Qsv(qc)=qe(i+nnpe3,j);          qc=qc+1;        Qsr(qc)=i4;         Qsc(qc)=j2;         Qsv(qc)=qe(i+nnpe3,j+nnpe);         qc=qc+1;        Qsr(qc)=i4;         Qsc(qc)=j3;         Qsv(qc)=qe(i+nnpe3,j+nnpe2);         qc=qc+1;        Qsr(qc)=i4;         Qsc(qc)=j4;         Qsv(qc)=qe(i+nnpe3,j+nnpe3);         qc=qc+1;                                                  endend