function [dndx,dndy,detj] = sfder(iele,nnpe,nqptv,dndxi,dndet,np,x,y)% compute shape function derivatives dndx = zeros(nnpe,nqptv);dndy = zeros(nnpe,nqptv);for i=1:1:nqptv  	      tjac(1,1)=0.0;      tjac(1,2)=0.0;      tjac(2,1)=0.0;                                                           tjac(2,2)=0.0; 	                                                                         for j=1:1:nnpe  	      j1=np(iele,j) ;  	        tjac(1,1)=tjac(1,1)+dndxi(j,i)*x(j1);                                 tjac(1,2)=tjac(1,2)+dndet(j,i)*x(j1);                                tjac(2,1)=tjac(2,1)+dndxi(j,i)*y(j1);                                tjac(2,2)=tjac(2,2)+dndet(j,i)*y(j1); 	    end                                                                                                                                   detj(i)=tjac(1,1)*tjac(2,2)-tjac(1,2)*tjac(2,1);	        tjaci(1,1)=tjac(2,2)/detj(i);                                            tjaci(1,2)=-tjac(1,2)/detj(i);                                           tjaci(2,1)=-tjac(2,1)/detj(i);                                           tjaci(2,2)=tjac(1,1)/detj(i);                                                                                                             for  j=1:1:nnpe	      dndx(j,i)=tjaci(1,1)*dndxi(j,i)+tjaci(2,1)*dndet(j,i);                  dndy(j,i)=tjaci(1,2)*dndxi(j,i)+tjaci(2,2)*dndet(j,i);	    end         end