function sf=sfn(q1,q2,n,meltyp)% shape functionsif(meltyp == 9)	switch n	case 1       sf = (2.0*q1-1.0)*(2.0*q2-1.0)*(q1-1.0)*(q2-1.0);    case 2       sf = 4.0*q1*(1.0-q1)*(q2-1.0)*(2.0*q2-1.0);    case 3       sf = q1*(2.0*q1-1.0)*(2.0*q2-1.0)*(q2-1.0);    case 4       sf = 4.0*q1*q2*(2.0*q1-1.0)*(1.0-q2);    case 5       sf = q1*q2*(2.0*q1-1.0)*(2.0*q2-1.0);    case 6       sf = 4.0*q1*q2*(1.0-q1)*(2.0*q2-1.0);    case 7       sf = (q1-1.0)*(2.0*q1-1.0)*(2.0*q2-1.0)*q2;    case 8       sf = 4.0*(2.0*q1-1.0)*(q1-1.0)*(1.0-q2)*q2;    case 9       sf = 16.0*q1*q2*(1.0-q1)*(1.0-q2);    endendif( meltyp == 8)	switch n    case 1      sf=2.0*(1.0-q1)*(1.0-q2)*(0.5-q1-q2);	    case 2      sf=4.0*q1*(1.0-q1)*(1.0-q2);	    case 3      sf=-2.0*q1*(1.0-q2)*(0.5-q1+q2);	    case 4       sf=4.0*q1*q2*(1.0-q2);		    case 5       sf=-2.0*q1*q2*(1.5-q1-q2);	    case 6       sf=4.0*q1*q2*(1.0-q1);	    case 7       sf=-2.0*q2*(1.0-q1)*(0.5+q1-q2);    case 8       sf=4.0*q2*(1.0-q1)*(1.0-q2);    endendif( meltyp == 6)	q3 = 1.0-q1-q2;switch n    case 1      sf=(2.0*q1-1.0)*q1;	    case 2      sf=4.0*q2*q1;	    case 3      sf=(2.0*q2-1.0)*q2;	    case 4       sf=4.0*q2*q3;		    case 5       sf=(2.0*q3-1.0)*q3;	    case 6       sf=4.0*q1*q3;	    endendif( meltyp == 4)	  switch n    case 1     sf=(1.0-q1)*(1.0-q2);	    case 2     sf=q1*(1.0-q2);	    case 3     sf=q1*q2;	    case 4     sf=(1.0-q1)*q2;	    endend                                                               if( meltyp == 3)	  switch n    case 1     sf=(1.0-q1-q2);	    case 2     sf=q2;	    case 3     sf=q1;		    endend                                                               