function  sn = ssfn(xi,i,meltyp) %  surface shape functions    if(meltyp==6)		      switch i		  case 1            sn=2.*xi*xi-3.0*xi+1.;		  case 2			sn=4.*xi-4.*xi*xi;		  case 3			sn=2.0*xi*xi-xi;		end			end    if(meltyp==8)		      switch i		  case 1            sn=2.*xi*xi-3.0*xi+1.;		  case 2			sn=4.*xi-4.*xi*xi;		  case 3			sn=2.0*xi*xi-xi;		end			end	    if(meltyp==9)		      switch i		  case 1            sn=2.*xi*xi-3.0*xi+1.;		  case 2			sn=4.*xi-4.*xi*xi;		  case 3			sn=2.0*xi*xi-xi;		end			end		if(meltyp==4)				switch i		case 1			sn=1.0-xi;		case 2			sn=xi;		end		end                                                                   		if(meltyp==3)				switch i		case 1			sn=1.0-xi;		case 2			sn=xi;		end		end                                                               