function [rjs,rtang] = surfjac(iele,ifrnp,nnps,nqpts,dnds,nnpe,np,x,y)%  computes surface jacobian termsfor i=1:1:nqpts	      r1=0.0;      r2=0.0;  for j=1:1:nnps	      j1=ifrnp+j;	        if(j1>nnpe)		  j1=1;	  end	        j2=np(iele,j1);	        r1=r1+dnds(j,i)*x(j2);      r2=r2+dnds(j,i)*y(j2);	    end        rjs(i)=sqrtm(r1^2+r2^2);      rtang(i)=atan2(r1,r2);	  end