function [ffx,ffy] =surforc(nnps,nqpts,swt,ssfac,rjs,rtang,surprs)% computes surface forces for pressure loadingffx = zeros(nnps);ffy = zeros(nnps);  for i=1:1:nqpts    tannor=rtang(i);    tracx=-surprs*cos(tannor);    tracy=+surprs*sin(tannor);	     xfacx=rjs(i)*swt(i)*tracx;     xfacy=rjs(i)*swt(i)*tracy;  for j=1:1:nnps	      ffx(j)=ffx(j)+ssfac(j,i)*xfacx;      ffy(j)=ffy(j)+ssfac(j,i)*xfacy;	    endend